let { Unit, units: u } = require('./out/index');
let { UnitConversionNetwork } = require ('./out/conversion-network');

u.acre = u.foot.mul(u.foot).mul(Unit.one(43560)).rename('acre');

let usDollar = Unit.base('USD');
let bag = Unit.base('bag');
let seed = Unit.base('seed');

let net = new UnitConversionNetwork();
net.addRate(1, bag, 250, usDollar);
net.addRate(1, bag, 63, u.pound);
net.addRate(6300, seed, 1, u.pound); 
net.addRate(2000, seed, 1, u.acre);

let conv = net.getConversionUnit(u.acre, usDollar);

let util = require('util');
console.log(util.inspect(net, { colors: true, depth: null, customInspect: false }));
